import React, { useState, useEffect } from "react";
import { Route, Routes, Navigate } from "react-router-dom";
import NavBar from "./components/NavBar/NavBar";
import HomePage from "./pages/HomePage";
import Login from "./components/Login/Login";
import Register from "./components/Register/Register";
import StarBackground from "./components/StarsBackground/StarsBackground";
import "./App.css";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Tracks authentication state
  const [userEmail, setUserEmail] = useState(""); // Tracks user's email for NavBar avatar

  // Check for token on page load to persist authentication
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      fetch("http://localhost:5000/api/users/check-auth", {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` }, // Attach token in request header
      })
        .then((res) => res.json())
        .then((data) => {
          if (data.isAuthenticated && data.email) {
            setIsAuthenticated(true);
            setUserEmail(data.email); // Store the user's email
          } else {
            setIsAuthenticated(false); // Reset auth if invalid response
          }
        })
        .catch((err) => {
          console.error("Authentication check failed:", err);
          setIsAuthenticated(false); // Reset auth on error
        });
    }
  }, []);

  // Logout function to reset authentication state and clear token
  const handleLogout = () => {
    localStorage.removeItem("token"); // Remove token from localStorage
    setIsAuthenticated(false); // Reset auth state
    setUserEmail(""); // Clear user's email
  };

  return (
    <div className="App">
      <StarBackground />
      <div className={`background-overlay ${!isAuthenticated ? "blur-active" : ""}`}></div>

      {/* Pass handleLogout and userEmail to NavBar */}
      {isAuthenticated && <NavBar onLogout={handleLogout} userEmail={userEmail} />}

      <Routes>
        {/* Login Route */}
        <Route
          path="/login"
          element={
            isAuthenticated ? <Navigate to="/" replace /> : <Login setAuth={setIsAuthenticated} />
          }
        />
        {/* Register Route */}
        <Route
          path="/register"
          element={
            isAuthenticated ? <Navigate to="/" replace /> : <Register setAuth={setIsAuthenticated} />
          }
        />
        {/* Home Route */}
        <Route
          path="/"
          element={
            isAuthenticated ? <HomePage /> : <Navigate to="/login" replace />
          }
        />
      </Routes>
    </div>
  );
}

export default App;
