import React, { useState, useEffect } from 'react';
import { BookOpen, LogOut, ChevronDown } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import './NavBar.css';

const NavBar = () => {
  const navigate = useNavigate();
  const [hidden, setHidden] = useState(false);
  const [lastScrollY, setLastScrollY] = useState(window.scrollY);

  const handleScroll = () => {
    const currentScrollY = window.scrollY;

    if (currentScrollY > lastScrollY && currentScrollY > 100) {
      setHidden(true); // Scroll down → Navbar moves up
    } else {
      setHidden(false); // Scroll up → Navbar returns
    }

    setLastScrollY(currentScrollY);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [lastScrollY]);

  return (
    <nav className={`navbar ${hidden ? "navbar-hidden" : ""}`}>
      <div className="navbar-container">
        {/* Left: User Avatar */}
        <div className="user-avatar">
          <div className="avatar-circle">U</div>
        </div>

        {/* Center: Clickable Logo */}
        <div className="navbar-logo-container" onClick={() => navigate('/')}>
          <BookOpen className="navbar-icon" />
          <span className="navbar-title">studHelp</span>
        </div>

        {/* Right: Logout Button */}
        <button className="logout-btn" onClick={() => navigate('/login')}>
          <LogOut className="logout-icon" />
        </button>
      </div>
    </nav>
  );
};

export default NavBar;
