import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import StarBackground from "../StarsBackground/StarsBackground";
import "./Register.css";

interface RegisterProps {
  setAuth: (authState: boolean) => void; // Prop to update auth state in App.tsx
}

const Register: React.FC<RegisterProps> = ({ setAuth }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!email || !password || !confirmPassword) {
      setError("Please fill in all fields!");
      return;
    }

    if (password !== confirmPassword) {
      setError("Passwords do not match!");
      return;
    }

    setIsLoading(true);
    setError("");

    try {
      const response = await fetch("http://localhost:5000/api/users/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem("token", data.token); // Save JWT for login persistence
        setAuth(true); // Update authentication state
        navigate("/"); // Redirect to homepage
      } else {
        setError(data.error || "Registration failed.");
      }
    } catch (error) {
      setError("Something went wrong during registration.");
      console.error("Registration error:", error);
    } finally {
      setIsLoading(false); // Stop loading spinner
    }
  };

  return (
    <div className={`register-page ${isLoading ? "processing" : ""}`}>
      <StarBackground />
      <div className="register-content">
        <form className="register-box" onSubmit={handleRegister}>
          <h2 className="register-title">Create an Account</h2>
          <p className="register-subtitle">Start exploring study resources</p>

          {error && <div className="error-message">{error}</div>}

          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            disabled={isLoading}
          />
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            disabled={isLoading}
          />
          <input
            type="password"
            placeholder="Confirm your password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            disabled={isLoading}
          />

          <button type="submit" className="register-btn" disabled={isLoading}>
            {isLoading ? "Registering..." : "Register"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Register;
