import React, { useState } from "react";
import { Mail, Lock, Loader2, KeyRound } from "lucide-react";
import { useNavigate } from "react-router-dom";
import StarBackground from "../StarsBackground/StarsBackground";
import "./Login.css";

interface LoginProps {
  setAuth: (authState: boolean) => void;
}

const Login: React.FC<LoginProps> = ({ setAuth }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async () => {
    if (!email || !password) {
      alert("Please fill in all fields!");
      return;
    }

    setIsLoading(true);

    try {
      const res = await fetch("http://localhost:5000/api/users/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (res.ok) {
        alert("Login successful!");
        setAuth(true);
        navigate("/");
      } else {
        alert(data.error || "Invalid email or password.");
      }
    } catch (err) {
      console.error(err);
      alert("Server error. Please try again.");
    }

    setIsLoading(false);
  };

  return (
    <div className={`register-page ${isLoading ? "processing" : ""}`}>
      <StarBackground />

      <div className="register-content">
        <div className="logo-container">
          <h1 className="app-logo">
            Stud<span className="help-text">Help</span>
          </h1>
        </div>

        <div className="register-box">
          <h2 className="register-title">Welcome Back</h2>
          <p className="register-subtitle">Login to continue your journey</p>

          <div className="input-group">
            <Mail className="input-icon" />
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              disabled={isLoading}
            />
          </div>

          <div className="input-group">
            <Lock className="input-icon" />
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              disabled={isLoading}
            />
          </div>

          <button className="register-btn" onClick={handleLogin} disabled={isLoading}>
            {isLoading ? (
              <Loader2 className="btn-icon spin" />
            ) : (
              <>
                <KeyRound className="btn-icon" /> Log In
              </>
            )}
          </button>

          <p className="login-text">
            Don’t have an account?{" "}
            <span className="login-link" onClick={() => navigate("/register")}>
              Sign up
            </span>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
