import React, { useState } from "react";
import { Mail, Lock, Loader2, UserPlus } from "lucide-react";
import { useNavigate } from "react-router-dom";
import StarBackground from "../StarsBackground/StarsBackground";
import "./Register.css";

interface RegisterProps {
  setAuth: (authState: boolean) => void;
}

const Register: React.FC<RegisterProps> = ({ setAuth }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleRegister = async () => {
    if (!email || !password || !confirmPassword) {
      alert("Please fill in all fields!");
      return;
    }

    if (password !== confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    setIsLoading(true);

    try {
      const response = await fetch("http://localhost:5000/api/users/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (!response.ok) {
        alert(data.error || "Registration failed.");
      } else {
        alert("Account created successfully!");
        setAuth(true);
        navigate("/");
      }
    } catch (error) {
      console.error("Registration error:", error);
      alert("Something went wrong during registration.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className={`register-page ${isLoading ? "processing" : ""}`}>
      <StarBackground />

      <div className="register-content">
        <div className="logo-container">
          <h1 className="app-logo">
            Stud<span className="help-text">Help</span>
          </h1>
        </div>

        <div className="register-box">
          <h2 className="register-title">Create an Account</h2>
          <p className="register-subtitle">Start exploring study resources</p>

          <div className="input-group">
            <Mail className="input-icon" />
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              disabled={isLoading}
            />
          </div>

          <div className="input-group">
            <Lock className="input-icon" />
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              disabled={isLoading}
            />
          </div>

          <div className="input-group">
            <Lock className="input-icon" />
            <input
              type="password"
              placeholder="Confirm your password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              disabled={isLoading}
            />
          </div>

          <button
            className="register-btn"
            onClick={handleRegister}
            disabled={isLoading}
          >
            {isLoading ? (
              <Loader2 className="btn-icon spin" />
            ) : (
              <>
                <UserPlus className="btn-icon" /> Sign Up
              </>
            )}
          </button>

          <p className="login-text">
            Already have an account?{" "}
            <span className="login-link" onClick={() => navigate("/login")}>
              Log in
            </span>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Register;
