import React, { useState } from "react";
import { Mail, Lock, Key, Loader2, UserPlus } from "lucide-react";
import { useNavigate } from "react-router-dom"; // Navigation
import StarBackground from "../StarsBackground/StarsBackground";
import "./Register.css";

interface RegisterProps {
  setAuth: (authState: boolean) => void;
}

const Register: React.FC<RegisterProps> = ({ setAuth }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate(); // Navigation hook

  const handleRegister = () => {
    if (!email || !password || !confirmPassword) {
      alert("Please fill in all fields!");
      return;
    }

    if (password !== confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    setIsLoading(true);

    setTimeout(() => {
      alert("Account created successfully!");
      setAuth(true); // Update authentication state
      navigate("/"); // Redirects to homepage after signup
      setIsLoading(false);
    }, 1500);
  };

  return (
    <div className={`register-page ${isLoading ? "processing" : ""}`}>
      <StarBackground />

      <div className="register-content">
        {/* StudHelp Logo */}
        <div className="logo-container">
          <h1 className="app-logo">Stud<span className="help-text">Help</span></h1>
        </div>

        <div className="register-box">
          <h2 className="register-title">Create an Account</h2>
          <p className="register-subtitle">Start exploring study resources</p>

          {/* Email Input */}
          <div className="input-group">
            <Mail className="input-icon" />
            <input 
              type="email" 
              placeholder="Enter your email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              disabled={isLoading}
            />
          </div>

          {/* Password Input */}
          <div className="input-group">
            <Lock className="input-icon" />
            <input 
              type="password" 
              placeholder="Enter your password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              disabled={isLoading}
            />
          </div>

          {/* Confirm Password Input */}
          <div className="input-group">
            <Lock className="input-icon" />
            <input 
              type="password" 
              placeholder="Confirm your password" 
              value={confirmPassword} 
              onChange={(e) => setConfirmPassword(e.target.value)} 
              disabled={isLoading}
            />
          </div>

          {/* Register Button */}
          <button className="register-btn" onClick={handleRegister} disabled={isLoading}>
            {isLoading ? (
              <Loader2 className="btn-icon spin" />
            ) : (
              <>
                <UserPlus className="btn-icon" /> Sign Up
              </>
            )}
          </button>

          {/* Login Prompt */}
          <p className="login-text">
            Already have an account? <span className="login-link" onClick={() => navigate("/login")}>Log in</span>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Register;
