import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

interface LoginProps {
  setAuth: (value: boolean) => void; // Authentication state updater from App.tsx
}

const Login: React.FC<LoginProps> = ({ setAuth }) => {
  const [inputs, setInputs] = useState({ email: "", password: "" });
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const { email, password } = inputs;

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInputs({ ...inputs, [e.target.name]: e.target.value });
  };

  const onSubmitForm = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true); // Start loading animation

    try {
      const response = await fetch("http://localhost:5000/api/users/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
        credentials: "include",
      });

      const parseRes = await response.json();

      if (response.ok && parseRes.message === "Login successful") {
        localStorage.setItem("token", parseRes.token); // Save JWT
        setAuth(true); // Update authentication state
        navigate("/"); // Redirect to homepage
      } else {
        alert(parseRes.error || "Invalid credentials!");
      }
    } catch (err) {
      console.error("Login error:", err);
      alert("Something went wrong. Please try again.");
    } finally {
      setIsLoading(false); // Stop loading animation
    }
  };

  return (
    <div className="login-container">
      <h1>Login</h1>
      <form onSubmit={onSubmitForm}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={email}
          onChange={onChange}
          className="login-input"
          disabled={isLoading}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={password}
          onChange={onChange}
          className="login-input"
          disabled={isLoading}
        />
        <button className="login-button" disabled={isLoading}>
          {isLoading ? "Logging in..." : "Login"}
        </button>
      </form>
    </div>
  );
};

export default Login;
