import React, { useState } from "react";
import { Route, Routes, Navigate } from "react-router-dom";
import NavBar from "./components/NavBar/NavBar";
import HomePage from "./pages/HomePage";
import Login from "./components/Login/Login";
import Register from "./components/Register/Register";
import StarBackground from "./components/StarsBackground/StarsBackground";
import "./App.css";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  return (
    <div className="App">
      {/* Star background for the entire app */}
      <StarBackground />

      {/* Conditionally blur background when on login page */}
      <div className={`background-overlay ${!isAuthenticated ? "blur-active" : ""}`}></div>

      {isAuthenticated && <NavBar />}

      <Routes>
        <Route path="/login" element={<Login setAuth={setIsAuthenticated} />} />
        <Route path="/" element={isAuthenticated ? <HomePage /> : <Navigate to="/login" replace />} />
        <Route path="/register" element={<Register setAuth={setIsAuthenticated} />} />
        <Route path="/" element={isAuthenticated ? <HomePage /> : <Navigate to="/login" replace />} />


      </Routes>
    </div>
  );
}

export default App;
