import React, { useState, useEffect } from "react";
import { BookOpen, LogOut } from "lucide-react";
import { useNavigate } from "react-router-dom";
import "./NavBar.css";

type NavBarProps = {
  onLogout: () => Promise<void>; // Logout function passed from parent
};

const NavBar = ({ onLogout }: NavBarProps) => {
  const navigate = useNavigate();
  const [hidden, setHidden] = useState(false);
  const [lastScrollY, setLastScrollY] = useState(window.scrollY);
  const [isLoggingOut, setIsLoggingOut] = useState(false);

  const handleScroll = () => {
    const currentScrollY = window.scrollY;

    if (currentScrollY > lastScrollY && currentScrollY > 100) {
      setHidden(true);
    } else {
      setHidden(false);
    }

    setLastScrollY(currentScrollY);
  };

  const handleLogout = async (e: React.MouseEvent<HTMLButtonElement>) => {
    e.preventDefault(); // Prevent default behavior
    setIsLoggingOut(true);

    try {
      await onLogout(); // Call the parent's logout function
      navigate("/login"); // Redirect to login page
    } catch (error) {
      console.error("Logout failed:", error);
    } finally {
      setIsLoggingOut(false);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [lastScrollY]);

  return (
    <nav className={`navbar ${hidden ? "navbar-hidden" : ""}`}>
      <div className="navbar-container">
        {/* User Avatar */}
        <div className="user-avatar">
          <div className="avatar-circle">U</div>
        </div>

        {/* Logo */}
        <div className="navbar-logo-container" onClick={() => navigate("/")}>
          <BookOpen className="navbar-icon" />
          <span className="navbar-title">studHelp</span>
        </div>

        {/* Logout Button */}
        <button
          className="logout-btn"
          onClick={handleLogout}
          disabled={isLoggingOut}
        >
          <LogOut className="logout-icon" />
          {isLoggingOut ? "Logging out..." : ""}
        </button>
      </div>
    </nav>
  );
};

export default NavBar;
