import React, { useState, useEffect } from "react";
import { Route, Routes, Navigate, useNavigate } from "react-router-dom";
import NavBar from "./components/NavBar/NavBar";
import HomePage from "./pages/HomePage";
import Login from "./components/Login/Login";
import Register from "./components/Register/Register";
import StarBackground from "./components/StarsBackground/StarsBackground";
import "./App.css";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  // Check authentication status on initial load
  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const response = await fetch('/api/users/check-auth', {
          credentials: 'include', // Includes cookies for authentication
        });

        if (!response.ok) throw new Error("Auth check failed");

        const data = await response.json();
        setIsAuthenticated(data.isAuthenticated);
        if (data.isAuthenticated) navigate('/'); // Redirect to homepage
      } catch (error) {
        console.error("Auth check error:", error);
        navigate('/login'); // Redirect to login if not authenticated
      } finally {
        setIsLoading(false); // Remove loading state
      }
    };

    checkAuthStatus();
  }, [navigate]);

  // Handle successful authentication (login/register)
  const handleAuthSuccess = () => {
    setIsAuthenticated(true); // Update auth state
    navigate('/'); // Redirect to homepage
  };

  // Handle logout functionality
  const handleLogout = async () => {
    try {
      const response = await fetch('/api/users/logout', {
        method: 'POST',
        credentials: 'include', // Includes session cookies
      });

      if (response.ok) {
        setIsAuthenticated(false); // Reset auth state
        navigate('/login'); // Redirect to login page
      } else {
        throw new Error('Logout failed');
      }
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  // Display loading screen while checking auth status
  if (isLoading) {
    return <div className="loading-screen">Loading...</div>;
  }

  return (
    <div className="App">
      <StarBackground />
      <div className={`background-overlay ${!isAuthenticated ? "blur-active" : ""}`}></div>

      {/* Render NavBar only when authenticated */}
      {isAuthenticated && <NavBar onLogout={handleLogout} />}

      <Routes>
        {/* Login Route */}
        <Route
          path="/login"
          element={
            isAuthenticated ? (
              <Navigate to="/" replace /> // Redirect to homepage if authenticated
            ) : (
              <Login onAuthSuccess={handleAuthSuccess} /> // Pass callback for successful login
            )
          }
        />

        {/* Register Route */}
        <Route
          path="/register"
          element={
            isAuthenticated ? (
              <Navigate to="/" replace /> // Redirect to homepage if authenticated
            ) : (
              <Register onAuthSuccess={handleAuthSuccess} /> // Pass callback for successful registration
            )
          }
        />

        {/* Home Route */}
        <Route
          path="/"
          element={
            isAuthenticated ? <HomePage /> : <Navigate to="/login" replace /> // Redirect to login if unauthenticated
          }
        />
      </Routes>
    </div>
  );
}

export default App;
