import React, { useState, useEffect } from "react";
import { Route, Routes, Navigate } from "react-router-dom";
import NavBar from "./components/NavBar/NavBar";
import HomePage from "./pages/HomePage";
import Login from "./components/Login/Login";
import Register from "./components/Register/Register";
import StarBackground from "./components/StarsBackground/StarsBackground";
import "./App.css";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userEmail, setUserEmail] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (token) {
      fetch("http://localhost:5000/api/users/check-auth", {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => res.json())
        .then((data) => {
          if (data.isAuthenticated && data.email) {
            setIsAuthenticated(true);
            setUserEmail(data.email);
          } else {
            localStorage.removeItem("token");
            setIsAuthenticated(false);
            setUserEmail("");
          }
        })
        .catch(() => {
          localStorage.removeItem("token");
          setIsAuthenticated(false);
          setUserEmail("");
        });
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    setUserEmail("");
  };

  return (
    <div className="App">
      <StarBackground />
      <div className={`background-overlay ${!isAuthenticated ? "blur-active" : ""}`}></div>

      {isAuthenticated && <NavBar onLogout={handleLogout} userEmail={userEmail} />}

      <Routes>
        <Route
          path="/login"
          element={
            isAuthenticated ? <Navigate to="/" replace /> : <Login setAuth={setIsAuthenticated} />
          }
        />
        <Route
          path="/register"
          element={
            isAuthenticated ? <Navigate to="/" replace /> : <Register setAuth={setIsAuthenticated} />
          }
        />
        <Route
          path="/"
          element={
            isAuthenticated ? <HomePage /> : <Navigate to="/login" replace />
          }
        />
      </Routes>
    </div>
  );
}

export default App;
