import React, { useState, useEffect } from 'react';
import { BookOpen, LogOut, ChevronDown } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import './NavBar.css';

type NavBarProps = {
  onLogout: () => Promise<void>; // Add this prop type
};

const NavBar = ({ onLogout }: NavBarProps) => {
  const navigate = useNavigate();
  const [hidden, setHidden] = useState(false);
  const [lastScrollY, setLastScrollY] = useState(window.scrollY);
  const [isLoggingOut, setIsLoggingOut] = useState(false);

  const handleScroll = () => {
    const currentScrollY = window.scrollY;

    if (currentScrollY > lastScrollY && currentScrollY > 100) {
      setHidden(true);
    } else {
      setHidden(false);
    }

    setLastScrollY(currentScrollY);
  };

  const handleLogout = async () => {
    setIsLoggingOut(true);
    try {
      await onLogout(); // Call the parent's logout function
      navigate('/login');
    } catch (error) {
      console.error('Logout failed:', error);
    } finally {
      setIsLoggingOut(false);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [lastScrollY]);

  return (
    <nav className={`navbar ${hidden ? "navbar-hidden" : ""}`}>
      <div className="navbar-container">
        <div className="user-avatar">
          <div className="avatar-circle">U</div>
        </div>

        <div className="navbar-logo-container" onClick={() => navigate('/.login')}>
          <BookOpen className="navbar-icon" />
          <span className="navbar-title">studHelp</span>
        </div>

        <button 
          className="logout-btn" 
          onClick={handleLogout}
          disabled={isLoggingOut}
        >
          <LogOut className="logout-icon" />
          {isLoggingOut ? 'Logging out...' : ''}
        </button>
      </div>
    </nav>
  );
};

export default NavBar;
